import { useState, useRef, useEffect } from "react";
import TodoList from "./TodoList";
import { v4 as uuidv4 } from "uuid";

function App() {
  const [todos, setTodos] = useState([]);

  const todoNameRef = useRef();

  // üîπ „Éö„Éº„Ç∏Ë™≠„ÅøËæº„ÅøÊôÇ„Å´ localStorage „Åã„ÇâË™≠„ÅøËæº„ÇÄ
  useEffect(() => {
    const storedTodos = JSON.parse(localStorage.getItem("todos"));
    if (storedTodos) setTodos(storedTodos);
  }, []);

  // üîπ todos „ÅåÂ§â„Çè„Å£„Åü„Çâ localStorage „Å´‰øùÂ≠ò
  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const handleAddTodo = () => {
    const name = todoNameRef.current.value;
    if (name === "") return;
    setTodos((prevTodos) => [
      ...prevTodos,
      { id: uuidv4(), name: name, completed: false },
    ]);
    todoNameRef.current.value = null;
  };

  const toggleTodo = (id) => {
    const newTodos = [...todos];
    const todo = newTodos.find((todo) => todo.id === id);
    todo.completed = !todo.completed;
    setTodos(newTodos);
  };

  const handleClear = () => {
    const newTodos = todos.filter((todo) => !todo.completed);
    setTodos(newTodos);
  };

  return (
    <div>
      <h1>React Todo App</h1>
      <TodoList todos={todos} toggleTodo={toggleTodo} />
      <input type="text" ref={todoNameRef} placeholder="„Çø„Çπ„ÇØ„ÇíÂÖ•Âäõ" />
      <button onClick={handleAddTodo}>„Çø„Çπ„ÇØ„ÇíËøΩÂä†</button>
      <button onClick={handleClear}>ÂÆå‰∫Ü„Åó„Åü„Çø„Çπ„ÇØ„ÅÆÂâäÈô§</button>
      <div>ÊÆã„Çä„ÅÆ„Çø„Çπ„ÇØ: {todos.filter((todo) => !todo.completed).length}</div>
    </div>
  );
}

export default App;
