import { useState, useRef, useEffect } from "react";
import TodoList from "./TodoList";
import { v4 as uuidv4 } from "uuid";

function App() {
  // üîπ ÂàùÂõû„É≠„Éº„ÉâÊôÇ„Å´ localStorage „Åã„ÇâË™≠„ÅøËæº„ÇÄ
  const getInitialTodos = () => {
    const storedTodos = localStorage.getItem("todos");
    return storedTodos ? JSON.parse(storedTodos) : [];
  };

  const [todos, setTodos] = useState(getInitialTodos);
  const [filter, setFilter] = useState("all"); // ‚Üê „Éï„Ç£„É´„Çø„ÉºÁî®„ÅÆ state „ÇíËøΩÂä†

  const todoNameRef = useRef();

  // üîπ todos „ÅåÂ§â„Çè„Çã„Åü„Å≥„Å´ localStorage „Å´‰øùÂ≠ò
  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  // „Çø„Çπ„ÇØ„ÇíËøΩÂä†
  const handleAddTodo = () => {
    const name = todoNameRef.current.value.trim();
    if (name === "") return;
    setTodos((prevTodos) => [
      ...prevTodos,
      { id: uuidv4(), name: name, completed: false },
    ]);
    todoNameRef.current.value = "";
  };

  // „Çø„Çπ„ÇØÂÆå‰∫ÜÁä∂ÊÖã„ÅÆÂàá„ÇäÊõø„Åà
  const toggleTodo = (id) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  // ÂÆå‰∫Ü„Çø„Çπ„ÇØ„ÅÆÂâäÈô§
  const handleClear = () => {
    setTodos((prevTodos) => prevTodos.filter((todo) => !todo.completed));
  };

  // üîπ „Éï„Ç£„É´„Çø„É™„É≥„Ç∞„Åï„Çå„Åü„Çø„Çπ„ÇØ‰∏ÄË¶ß
  const filteredTodos = todos.filter((todo) => {
    if (filter === "active") return !todo.completed;
    if (filter === "completed") return todo.completed;
    return true; // all
  });

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
      <h1>React Todo App</h1>

      {/* TodoList „Å´„Éï„Ç£„É´„Çø„ÉºÂæå„ÅÆ„É™„Çπ„Éà„ÇíÊ∏°„Åô */}
      <TodoList todos={filteredTodos} toggleTodo={toggleTodo} />

      <input
        type="text"
        ref={todoNameRef}
        placeholder="„Çø„Çπ„ÇØ„ÇíÂÖ•Âäõ"
        style={{ width: "70%", marginRight: "5px" }}
      />
      <button onClick={handleAddTodo}>ËøΩÂä†</button>
      <button onClick={handleClear} style={{ marginLeft: "5px" }}>
        ÂÆå‰∫Ü„Çø„Çπ„ÇØÂâäÈô§
      </button>

      {/* „Éï„Ç£„É´„Çø„Éº„Éú„Çø„É≥ */}
      <div style={{ marginTop: "10px" }}>
        <span>Ë°®Á§∫: </span>
        <button onClick={() => setFilter("all")}>„Åô„Åπ„Å¶</button>
        <button onClick={() => setFilter("active")}>Êú™ÂÆå‰∫Ü</button>
        <button onClick={() => setFilter("completed")}>ÂÆå‰∫ÜÊ∏à„Åø</button>
      </div>

      <div style={{ marginTop: "10px" }}>
        ÊÆã„Çä„ÅÆ„Çø„Çπ„ÇØ: {todos.filter((todo) => !todo.completed).length}
      </div>
    </div>
  );
}

export default App;
